import React, { Component } from 'react'

export class NewsItem extends Component {
  article= [
    {
      article_id: "6106cf1ccb0ef01ebf9d40640adbd106",
      title: "Do You Experience Sunshine Guilt?",
      description: "Have you ever felt guilty for staying indoors on a warm, sunny day? Then you might have fallen victim to 'sunshine guilt.'",
      imageurl: "https://www.vice.com/wp-content/uploads/sites/2/2025/04/do-you-experience-sunshine-guilt.jpg?w=1024",
      link: "https://www.vice.com/en/article/do-you-experience-sunshine-guilt/",
    },
    {
      article_id: "4b98d547fa3f9e86bc2689f8375315bf",
      title: "Lisa Vanderpump finally says she ‘can’t support’ James Kennedy after domestic violence arrest",
      description: "Lisa Vanderpump has finally spoken out about James Kennedy's arrest, saying she 'can’t support' him anymore.",
      imageurl: "https://pagesix.com/wp-content/uploads/sites/3/2025/04/lisa-vanderpump.jpg",
      link: "https://pagesix.com/2025/04/16/celebrity-news/lisa-vanderpump-finally-says-she-cant-support-james-kennedy-after-arrest/",
    }
  ]
  constructor(){
    super();
    console.log("Hello i am a Constructor");
    this.state={
      article:this.article,
    loading:false
    }
  }
  render() {
    let {title,discription,imageurl,newsurl}=this.props
    return (
      <div className='my-3'>
        <div className="card" style={{ width: "18rem" }}>
        <img src={imageurl} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">
              {discription}
            </p>
            <a href="/news detail" className="btn btn-sm btn-primary">
              Read More
            </a>
          </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
